import pytestimport dicedef test_dice(dice_instance):    assert isinstance(dice_instance, dice.Dice)def test_dice_has_edges(tetrahedron, tetrahedron_edges):    assert tetrahedron.edges == tetrahedron_edges@pytest.fixturedef dice_instance():    instance = dice.Dice(0)    return instance@pytest.fixturedef tetrahedron():    instance = dice.Dice(4)    return instance@pytest.fixturedef tetrahedron_edges():    edges = list(range(1, 5))    return edges